import React from 'react';
import './App.css';
import DetailsContainer from './components/details';
import FiltersContainer from './components/filter-container';
import SearchContainer from './components/search';
import Pagination from './components/pagination';

class App extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			sort: "desc",
			page: 1,
			language: "javascript",
			searchParams: "",
			results: {}
		}
		this.getGitHubData();
	}
	getGitHubData(){
		/*fetch(`https://api.github.com/search/repositories?q=tetris+language:{this.state.language}&sort=stars&order=${this.state.sort}`)
			.then(response => response.json())
			.then(res => {
				this.setState({...this.state, results: res});
			});*/
	}

	renderRepoList(){
		return (
			this.state.results.map(data => {
				return <DetailsContainer Name={data.name} Owner={data.owner.login} Fork={data.forks} Star={data.stargazers_count}/>
			})
		);
	}

	render(){
		return (
		    <div className="App">
		      Learning React
		      	<SearchContainer/>
		      	<FiltersContainer/>
		 		{this.renderRepoList()}
		 		<Pagination/>
		    </div>
		);
	}
}

export default App;
