import React from 'react';
import './App.css';
import DetailsContainer from './components/details';
import FiltersContainer from './components/filter-container';
import SearchContainer from './components/search';
import Pagination from './components/pagination';

class App extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			sort: "desc",
			page: 1,
			language: "javascript",
			searchParams: ""
		}
	}
	getGitHubData(){
		fetch(`https://api.github.com/search/repositories?q=tetris+language:{this.state.language}&sort=stars&order=${this.state.sort}`)
	}

	render(){
		return (
		    <div className="App">
		      Learning React
		      	<SearchContainer/>
		      	<FiltersContainer/>
		 		<DetailsContainer/>
		 		<Pagination/>
		    </div>
		);
	}
}

export default App;
